.nameContainer{
    z-index: 9999;
    width: 25vw;
    height: 60vh;
    position: relative;
    margin: 0;
    padding: 0;
    top: 10vh;
    left: 5vw;
    /*background-color: white;*/
    word-wrap: break-all;
}
#firstName {
    z-index:9999;
    position: relative;
    left: 0;
    top:12vh;
    font-family: 'Poppins';
    font-style: normal;
    font-weight: 600;
    font-size: 4.5vw;
    display: flex;
    align-items: center;
    letter-spacing: 2.8vw;
    /*background-color: red;*/
    word-break: break-all;
    word-wrap: break-word;
    color: #D6D6D6;
}
#lastName {
    z-index:9999;
    position: relative;
    left: 0;
    top: 12vh;
    font-family: 'Poppins';
    font-style: normal;
    font-weight: 600;
    font-size: 6.5vw;
    line-height: 9vw;
    display: flex;
    align-items: center;
    letter-spacing: 0.39em;
    word-break: break-all;
    word-wrap: break-word;
    color: #212121;
}
#bar {
    position: fixed;
    z-index:9999;
    top:4vh;
    margin: 0 auto;
    width: 100%;
    height: .4vh;
    background-color: #E02929;
}
#logo {
    position: fixed;
    z-index:9999;
    top:1vh;
    left: 5.5vw;
    margin: 0 auto;
    padding: 0;
    width: 100%;
    height: .4vh;
    color: #E02929;
    font-style: 'Poppins';
    font-weight: 600;
    font-size: 1vw;
}

